mapper.update:

    UPDATE kb_procedure SET sort_number = sort_number+1

    WHERE sort_number >= #{currentOrderNumber} AND sort_number<=#{originOrderNumber}




    UPDATE kb_procedure SET sort_number = sort_number-1

    WHERE sort_number <= #{currentOrderNumber} AND sort_number>=#{originOrderNumber}



AND board_id=#{boardId} AND delete_status=0 AND id != #{id}
